async function navigateToAuthStart() {
    /**
     * Navigate to the authentication start page.
     * 
     * This function navigates the browser to the authentication start page.
     */
    try {
        logger.info("Navigating to the authentication start page");
        await this.page.goto('https://junction.welocalize.xyz/auth/start');
    } catch (error) {
        logger.error(`Failed to navigate to auth start: ${error}`);
        throw error;
    }
}

async function clickAuthenticate() {
    /**
     * Click the authenticate button.
     * 
     * This function clicks the authenticate button on the page.
     */
    try {
        logger.info("Clicking the authenticate button");
        await this.page.locator(locators.authenticateButton).click();
    } catch (error) {
        logger.error(`Failed to click authenticate button: ${error}`);
        throw error;
    }
}

async function fillUsername(username) {
    /**
     * Fill the username textbox.
     * 
     * This function fills the username textbox with the provided username.
     */
    try {
        logger.info(`Filling username: ${username}`);
        await this.page.locator(locators.usernameTextbox).fill(username);
    } catch (error) {
        logger.error(`Failed to fill username: ${error}`);
        throw error;
    }
}

async function clickNext() {
    /**
     * Click the next button.
     * 
     * This function clicks the next button on the page.
     */
    try {
        logger.info("Clicking the next button");
        await this.page.locator(locators.nextButton).click();
    } catch (error) {
        logger.error(`Failed to click next button: ${error}`);
        throw error;
    }
}

