async function updatePrimaryEmail(email) {
    /**
     * Update the primary contact email.
     * 
     * This function updates the primary contact email with the provided email address.
     */
    try {
        logger.info(`Updating primary email to: ${email}`);
        await this.page.locator(locators.primaryEmailTextbox).fill(email);
        await this.page.locator(locators.updateButton).click();
    } catch (error) {
        logger.error(`Failed to update primary email: ${error}`);
        throw error;
