async function testAddUniquePrimaryEmail() {
    /**
     * Test case for adding a unique primary email address.
     * 
     * This function executes the test case for adding a unique primary email address.
     */
    try {
        logger.info("Starting test case: Add a unique primary email address");
        await this.navigateToAuthStart();
        await this.clickAuthenticate();
        await this.fillUsername('randomy01@proton.me');
        await this.clickNext();
        await this.fillPassword('Localize1');
        await this.clickVerify();
        await this.navigateToDashboard();
        await this.clickProfile();
        await this.updatePrimaryEmail('ete-unique-100Percentage@proton.me');
        await this.clickOptionsAndLogout();
    } catch (error) {
        logger.error(`Test case failed: ${error}`);
        throw error;
    }
}

